import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { RootState } from '../store';
interface FilterState {
  show: boolean;
}
const initialState: FilterState = {
  show: boolean,
};

export const filterSlice = createSlice({
  name: 'filter',
  initialState,
  reducers: {
    setFilterShow: (state, action: PayloadAction<FilterState['setShow']>) => {
      state.setShow = action.payload;
    },
  },
});

export const { setFilterShow } = filterSlice.actions;
export const selectFilterShow = (state: RootState) => state.filter.setShow;
export default filterSlice.reducer;
